import React from "react";


// ================================================================================== H2Grediant ==================================================================================
export function H2Grediant({ children, className = "" }) {
  return (
    <h2 className={`text-3xl md:text-5xl font-extrabold text-transparent bg-clip-text bg-gradient-animated bg-[length:200%_200%] animate-gradient-slide tracking-tight inline-block mb-10 ${className}`}>
      {children}
    </h2>
  );
}

// ================================================================================== H2 ==================================================================================
export function H2({ children, className = "" }) {
  return (
    <h2 className={`text-3xl md:text-4xl font-semibold text-black mb-4 ${className}`}>
      {children}
    </h2>
  );
}
// ================================================================================== H3Grediant ==================================================================================
export function H3Grediant({ children, className = "" }) {
  return (
    <h3 className={`text-2xl font-semibold text-primaryTeal mb-4 ${className}`}>{children}</h3>
  );
}

// ================================================================================== H3 ==================================================================================
export function H3({ children }) {
  return (
    <h3 className="text-2xl font-semibold text-gray-800 mb-4">{children}</h3>
  );
}

export function Button ({children}){
  return(
    <button className="bg-primaryTeal">
      {children}
    </button>
  )
}
const eleProvider = () => {
  return <></>;
};

export default eleProvider;




